%w1=[1;1];b1=[0;-0.5];w2=[2 -4];b2=0;p=-2:.1:2;%a1=purelin(w2*poslin(w1*p,b1),b2);a1=w2*poslin(w1*p+b1*ones(1,length(p)))+b2*ones(1,length(p));figure(1)clf resetplot(p,a1,'b','lineWidth',2)axis([-2 2 -3 3]);hold onset(gcf,'inverthardcopy','off')set(gca,'XColor',[0 0 0]);set(gca,'YColor',[0 0 0]);set(gca,'xtick',[-2 -1 0 1 2])set(gca,'ytick',[-3 -2 -1 0 1 2 3])set(gcf,'Color',[1 1 1]);hold offfigure(2)clf reseta=a1';w2t=w2;for dw=-1:.5:1,  w2t(1)=w2(1)+dw;  %a1t=purelin(w2t*poslin(w1*p,b1),b2);  a1t=w2t*poslin(w1*p+b1*ones(1,length(p)))+b2*ones(1,length(p));  a=[a a1t'];endsubplot(2,2,2), plot(p,a,'k')axis([-2 2 -3 3]);text(-1.5,2.4,'{\fontname{times}\fontsize{14}\itw}^{\fontname{times}\fontsize{10}2}_{\fontname{times}\fontsize{10}1,1}')xlabel('\fontname{times}\fontsize{14}(b)')hold onsubplot(2,2,2), plot(p,a1,'b','lineWidth',2)set(gcf,'inverthardcopy','off')set(gca,'XColor',[0 0 0]);set(gca,'YColor',[0 0 0]);set(gca,'xtick',[-2 -1 0 1 2])set(gca,'ytick',[-3 -2 -1 0 1 2 3])set(gcf,'Color',[1 1 1]);hold off%pausea=a1';b1t=b1;for db=-1:.5:1,  b1t(2)=b1(2)+db;  %a1t=purelin(w2*poslin(w1*p,b1t),b2);  a1t=w2*poslin(w1*p+b1t*ones(1,length(p)))+b2*ones(1,length(p));  a=[a a1t'];endsubplot(2,2,1), plot(p,a,'k')axis([-2 2 -3 3]);%text(-1.5,2.5,'{\itb}^{1}_{2}')%xlabel('(a)')text(-1.5,2.4,'{\fontname{times}\fontsize{14}\itb}^{\fontname{times}\fontsize{10}1}_{\fontname{times}\fontsize{10}2}')xlabel('\fontname{times}\fontsize{14}(a)')hold onsubplot(2,2,1), plot(p,a1,'b','lineWidth',2)set(gcf,'inverthardcopy','off')set(gca,'XColor',[0 0 0]);set(gca,'YColor',[0 0 0]);set(gca,'xtick',[-2 -1 0 1 2])set(gca,'ytick',[-3 -2 -1 0 1 2 3])set(gcf,'Color',[1 1 1]);hold off%pausea=a1';w2t=w2;for dw=-1:.5:1,  w2t(2)=w2(2)+dw;  %a1t=purelin(w2t*poslin(w1*p,b1),b2);  a1t=w2t*poslin(w1*p+b1*ones(1,length(p)))+b2*ones(1,length(p));  a=[a a1t'];endsubplot(2,2,3), plot(p,a,'k')axis([-2 2 -3 3]);%text(-1.5,2.5,'{\itw}^{2}_{1,2}')%xlabel('(c)')text(-1.5,2.4,'{\fontname{times}\fontsize{14}\itw}^{\fontname{times}\fontsize{10}2}_{\fontname{times}\fontsize{10}1,2}')xlabel('\fontname{times}\fontsize{14}(c)')hold onsubplot(2,2,3), plot(p,a1,'b','lineWidth',2)set(gcf,'inverthardcopy','off')set(gca,'XColor',[0 0 0]);set(gca,'YColor',[0 0 0]);set(gca,'xtick',[-2 -1 0 1 2])set(gca,'ytick',[-3 -2 -1 0 1 2 3])set(gcf,'Color',[1 1 1]);hold off%pausea=a1';b2t=b2;for db=-1:0.5:1,  b2t(1)=b2(1)+db;  %a1t=purelin(w2*poslin(w1*p,b1),b2t);  a1t=w2*poslin(w1*p+b1*ones(1,length(p)))+b2t*ones(1,length(p));  a=[a a1t'];endsubplot(2,2,4), plot(p,a,'k')axis([-2 2 -3 3]);%text(-1.5,2.5,'{\itb}^{2}')%xlabel('(d)')text(-1.5,2.5,'{\fontname{times}\fontsize{14}\itb}^{\fontname{times}\fontsize{10}2}')xlabel('\fontname{times}\fontsize{14}(d)')hold onsubplot(2,2,4), plot(p,a1,'b','lineWidth',2)set(gcf,'inverthardcopy','off')set(gca,'XColor',[0 0 0]);set(gca,'YColor',[0 0 0]);set(gca,'xtick',[-2 -1 0 1 2])set(gca,'ytick',[-3 -2 -1 0 1 2 3])set(gcf,'Color',[1 1 1]);subplot(2,2,1); text(5,5,'{\itAe}^{-\alpha\itt}')hold off% figure(3)% clf reset% g=sin(pi*p/4)+1;% plot(p,g,'b')% axis([-2 2 -1 3]);% hold on% set(gcf,'inverthardcopy','off')% % set(gca,'XColor',[0 0 0]);% set(gca,'YColor',[0 0 0]);% set(gca,'xtick',[-2 -1 0 1 2])% set(gca,'ytick',[-1 0 1 2 3])% set(gcf,'Color',[1 1 1]);% % hold off