function pattern_recog_poslin()w1=[1 1;-1 -1;-1 1;1 -1];b1=[-1;3;1;1];w2=-[1 1 1 1];b2=5;p1 = -1:.1:3;p2 = -1:.1:3;[P1,P2] = meshgrid(p1,p2);[n1,n2] = size(P1);nump = n1*n2;pp1 = reshape(P1,1,nump);pp2 = reshape(P2,1,nump);p = [pp1;pp2];%p=-2:.1:2;%a1=purelin(w2*poslin(w1*p,b1),b2);a=w2*poslin(w1*p+b1*ones(1,nump))+b2*ones(1,nump);aa = reshape(a,n1,n2);figure(1)contourf(P1,P2,aa,[0 0])axis('square')ch = get(gca,'children');ch2=get(ch,'children');for i=1:length(ch2),set(ch2(i),'facecolor','blue')set(ch2(i),'edgecolor','blue')endhold onax = axis;plot([0 0],[ax(1) ax(2)],'k',[ax(3) ax(4)],[0 0],'k')hold off%print -depsc -tiff -r600 poslinNetDBfigure(3)% Plot surfacecax = gca;hs=surf(cax,P1,P2,aa);% axis([limit1 limit2 limit1 limit2 -2 1]);% [az,el]=view;% az1=az+90;% view([az1 el]) colormap('gray')hold('on');a = get(cax,'zlim');zpos = a(1); % Always put contour below plot.% Get D contour data[cc,hh] = contour3(cax,P1,P2,aa,[0 0]);%%% size zpos to match the datafor i = 1:length(hh)        zz = get(hh(i),'Zdata');        set(hh(i),'Zdata',zpos*ones(size(zz)));endhand=mesh(P1,P2,0*ones(size(aa)));%set(hand,'EdgeAlpha',0.5)set(hand,'facecolor','none')hold off%print -depsc -tiff -r600 poslinNetSurf